  while (++index < maxLength) {
    var verValue = version[index],
        othValue = other[index];

    if (verValue > othValue) {
      return 1;
    }
    if (verValue < othValue) {
      return -1;
    }
  }
  return 0;
}

/**
 * Resolves the source map path from the given output path.
