 * @private
 * @param {Object} [error] The error object.
 * @param {Buffer} result The gzipped source buffer.
 */
function onUglifyGzip(error, result) {
  if (error) {
    throw error;
  }
  if (result != null) {
    if (!this.isSilent) {
      console.log('Done. Size: %d bytes.', _.size(result));
    }
